sys_script_client.script:
function onSubmit() {
	//Type appropriate comment here, and begin script below

	var entered_date = new Date(g_form.getValue('u_when_needed'));
	var current_date = new Date();

	if ( g_form.isNewRecord())
		{
		if (entered_date< current_date)
			{
			alert("When needed date should be greater then today's date");
			//g_form.clearValue('u_when_needed');
			return false;
		    }

		else
			{

			alert("Thank you. Your request number " + g_form.getValue('number') +  " was successfully submitted by " + g_user.getFullName());
		    }
	     }

	else if ( !g_form.isNewRecord() & g_form.getValue('state') =='3')
	{
		g_form.setDisabled('state', true);
		alert(" Your request number " + g_form.getValue('number') +  " is Closed by " + g_user.getFullName());
	}

	else 
		{
	alert("Thank you. Your request number " + g_form.getValue('number') +  " was successfully    updated by " + g_user.getFullName());
		}


}

