function onSubmit() {
	//Type appropriate comment here, and begin script below

	var entered_date = new Date(g_form.getValue('u_when_needed'));
	var current_date = new Date();

	if (g_form.isNewRecord() & entered_date> current_date)
		{

	g_form.addInfoMessage("Thank you. Your request number " + g_form.getValue('number') +  " was successfully     submitted by " + g_user.getFullName());


			//return true;
	}
	else
		{
		g_form.addInfoMessage("When needed date should be greater then today's date");
		g_form.clearValue('u_when_needed');
		return false;
	}

	var x = 7;
	jslog("This message is from jslog().");
	jslog("The value of x = " + x);
	jslog("The NeedIt State value = " + g_form.getValue('state'));
	jslog("The currently logged in user is " + g_user.getFullName() + ".");





}
